import React, { useState } from 'react';
import { CurrencyIcon, Counter } from '@ya.praktikum/react-developer-burger-ui-components';
import ModalOverlay from '../ModalOverlay/ModalOverlay'; // Подключите компонент ModalOverlay

import styles from './BurgerIngredient.module.css';

function BurgerIngredient({ image, price, name }) {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleIngredientClick = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <>
      <div className={styles.BurgerIngredient} onClick={handleIngredientClick}>
        <img src={image} alt="Ингредиент бургера" />
        <Counter count={1} size="default" extraClass="m-1" />
        <h2 className={styles.IngredientPrice}>
          {price} <CurrencyIcon type="primary" />
        </h2>
        <p className={styles.IngredientName}>{name}</p>
      </div>

      {isModalOpen && <ModalOverlay onClick={closeModal} />}
    </>
  );
}

export default BurgerIngredient;
