import React from 'react'
import {data} from '../../utils/data'
import styles from "../BurgerConstructor/BurgerConstructor.module.css";


import {
    ConstructorElement
  } from "@ya.praktikum/react-developer-burger-ui-components";

function BurgerConstructor() {
    return (
      <div className={styles.BurgerConstructor}>
      <ConstructorElement   type="top"
        isLocked={true}
        text={data[0].name}

        thumbnail={data[0].image}   price ={data[0].price}/>
      <div style={{
  display: 'flex',
  flexDirection: 'column',
  maxWidth: 580,
  height: 464,
  overflowY: 'scroll',
  gap: 16,
  scrollbarWidth: 'thin', // Для Firefox
  scrollbarColor: 'var(--text-dark-grey-secondary-text, #8585AD) var(--text-dark-grey-secondary-text, #8585AD)', // Для Firefox
  WebkitScrollbarWidth: 'thin', // Для WebKit (Chrome, Safari)
  WebkitScrollbarColor: 'var(--text-dark-grey-secondary-text, #8585AD) var(--text-dark-grey-secondary-text, #8585AD)', // Для WebKit (Chrome, Safari)
}}>
      <ConstructorElement 
        text={data[1].name}
        price={data[1].price}
        thumbnail={data[1].image}
        />
        <ConstructorElement 
        text={data[2].name}
        price={data[2].price}
        thumbnail={data[2].image}
        />
        <ConstructorElement 
        text={data[3].name}
        price={data[3].price}
        thumbnail={data[3].image}
        />
        <ConstructorElement 
        text={data[4].name}
        price={data[4].price}
        thumbnail={data[4].image}
        />
        <ConstructorElement 
        text={data[5].name}
        price={data[5].price}
        thumbnail={data[5].image}
        />

      </div>
      <ConstructorElement
        type="bottom"
        isLocked={true}
        text={data[0].name}
        price={data[0].price}
        thumbnail={data[0].image}/>
      </div>
    );
  }







export default BurgerConstructor
