import React from 'react'
import {data} from '../../utils/data'
import styles from "../BurgerIngredients/BurgerIngredients.module.css";
import BurgerIngredient from "../../components/BurgerIngredient/BurgerIngredient"

function BurgerIngredients() {
    return (
      <div className={styles.BurgerIngredients}>
        <h2 style={{ marginLeft: 0 }}>Соберите бургер</h2>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)', borderBottom: '1px solid #ccc' }}>
          <h3 style={{ textAlign: "center" }}>Булки</h3>
          {/* Сюда добавьте компоненты для отображения булок */}
        </div>
        <BurgerIngredient />
        <BurgerIngredient />

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)' }}>
          <h3 style={{ textAlign: "center" }}>Соусы</h3>
          {/* Сюда добавьте компоненты для отображения соусов */}
        </div>
        <BurgerIngredient />
        <BurgerIngredient />

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(2, 1fr)' }}>
          <h3 style={{ textAlign: "center" }}>Начинки</h3>
          {/* Сюда добавьте компоненты для отображения начинок */}
        </div>
        <BurgerIngredient />
        <BurgerIngredient />
      </div>
    );
  }


export default BurgerIngredients
