import React, { useState, useCallback } from "react";

import { data } from "../../utils/data";
import styles from "../BurgerConstructor/BurgerConstructor.module.css";
import Modal from "../Modal/Modal";
import PropTypes from "prop-types";
import {
  ConstructorElement,
  Button,
  CurrencyIcon,
} from "@ya.praktikum/react-developer-burger-ui-components";

import OrderDetails from "../OrderDetails/OrderDetails";

function BurgerConstructor() {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const handleModalClose = useCallback(() => setModalIsOpen(false), []);
  return (
    <div className={styles.BurgerConstructor}>
      <ConstructorElement
        type="top"
        isLocked={true}
        text={data[0].name}
        thumbnail={data[0].image}
        price={data[0].price}
      />
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          maxWidth: 580,
          height: 464,
          overflowY: "scroll",
          gap: 16,
        }}
      >
        <ConstructorElement
          text={data[1].name}
          price={data[1].price}
          thumbnail={data[1].image}
        />
        <ConstructorElement
          text={data[2].name}
          price={data[2].price}
          thumbnail={data[2].image}
        />
        <ConstructorElement
          text={data[3].name}
          price={data[3].price}
          thumbnail={data[3].image}
        />
        <ConstructorElement
          text={data[4].name}
          price={data[4].price}
          thumbnail={data[4].image}
        />
        <ConstructorElement
          text={data[5].name}
          price={data[5].price}
          thumbnail={data[5].image}
        />
      </div>
      <ConstructorElement
        type="bottom"
        isLocked={true}
        text={data[0].name}
        price={data[0].price}
        thumbnail={data[0].image}
      />

      <div
       className={styles.pricePlusOrder} price
      >
        <div className={styles.pricePlusOrder}>
          <h2 style={{fontSize: 48}}
          >
            610
          </h2>{" "}
          <CurrencyIcon style={{ minWidth: 36 }} type="primary" />{" "}
        </div>

        <Button
          type="primary"
          size="medium"
          onClick={() => setModalIsOpen(true)}
          htmlType="button"
        >
          Оформить заказ
        </Button>
      </div>

      {modalIsOpen && (
        <Modal onClose={handleModalClose} title={null}>
          <OrderDetails />
        </Modal>
      )}
    </div>
  );
}

// Определите PropTypes для свойств
BurgerConstructor.propTypes = {
  data: PropTypes.array.isRequired, // Пример проверки для свойства "data"
  modalIsOpen: PropTypes.bool.isRequired, // Пример проверки для свойства "modalIsOpen"
};

export default BurgerConstructor;
